        ></input>
        <button onClick={addNewErc721Token}>add new erc721</button>
          {NFTlist.map((token) => {
            return (
              <div className="col-3">
                <svg
                  className="bd-placeholder-img"
                  width="100%"
                  height="250"
                  xmlns="http://www.w3.org/2000/svg"
                  aria-label="Placeholder: Image"
                  preserveAspectRatio="xMidYMid slice"
                  role="img">
                </svg>
                {/* <h1 className="title">NFT</h1> */}
                <div class="card">
                  <img class="card-img-top" src={logo} alt={"logo"} />
                  <div class="card-body">
                    <h5 class="card-title">{token.Name}</h5>
                    <p class="card-text">설명: {token.des} 가격: {token.cost}</p>
                    <a href="/" class="btn btn-primary">구매</a>
                  </div>
                </div>
              </div>


            )
          })}
        </div>
      </div>
    </div>
  );
}

export default Main;
